mapper.update:
update student
name = #{name}, no = #{no}, phone = #{phone}, gender = #{gender}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, violation_count = #{violationCount}, violation_score = #{violationScore},
where id = #{id}

mapper.select:
select s.*, c.name clazzName, (case when s.degree=1 then '初中' when s.degree=2 then '高中' when s.degree=3 then '大专' when s.degree=4 then '本科' when s.degree=5 then '硕士' when s.degree=6 then '博士' else '其他' end ) degreeStr from student s left join clazz c on s.clazz_id = c.id
s.name like concat('%',#{studentQueryParam.name},'%')
and s.degree = #{studentQueryParam.degree}
and s.clazz_id = #{studentQueryParam.clazzId}
order by update_time desc
